import React from 'react';
import { useForm } from 'react-hook-form'
import './Forms.css';

  const Forms = () => {

  const Forms = useForm();
  console.log(Forms.watch("lastname"));
  console.log(Forms.formState.errors)

  const onSubmitForm = (data) => {
    console.log(data);
  }

  return (
    <form onSubmit={Form.handleSubmit(onSubmitForm())}>

      <input type="text" placeholder='firstname'  {...Form.register('firstname', {required:true})}/>
     <p>{Forms.formState.errors.firstname && Forms.formState.errors.firstname.type === "required" ? "this is required" :''}</p>

      <input type="text" placeholder='lastname'  {...Form.register('lastname', {required:true})}/>

      <button type='submit'>submit</button>
    </form>
  )
}
export default Forms;
